function openurl(a){window.location.href=a}function turnToPage(b){var a=document.getElementsByClassName("page");for(var c=a.length-1;c>=0;c--){a[c].style.visibility="hidden"}var d=document.getElementsByClassName("unselectable-page");for(var c=d.length-1;c>=0;c--){d[c].style.visibility="hidden"}document.getElementById(b).style.visibility="visible"}var plainText="";function setPlainText(){plainText=document.getElementById("plainText").textContent}function passwordChange(){if(document.getElementById("aespassword").value.length>0){document.getElementById("page-aes").getElementsByClassName("btn_next")[0].style.visibility="inherit"}else{document.getElementById("page-aes").getElementsByClassName("btn_next")[0].style.visibility="hidden"}}var selectedAlgorithmE=0;var result="";function setSelectedAlgorithmE(a){switch(a){case 0:turnToPage("page-randomText");break;case 1:turnToPage("page-caesar");break;case 2:result=Encrypt.base64(plainText);document.getElementById("result").textContent=result;turnToPage("page-result");break;case 3:result=Encrypt.dictionaryEncrypt(plainText,Encrypt.morseDict);document.getElementById("result").textContent=result;turnToPage("page-result");break;case 4:result=Encrypt.dictionaryEncrypt(plainText,Encrypt.emojiDict);document.getElementById("result").textContent=result;turnToPage("page-result");break;case 5:turnToPage("page-aes");default:break}selectedAlgorithmE=a}function plainTextInputChange(){if(document.getElementById("plainText").textContent.length>0){document.getElementById("ph-plainText").style.visibility="hidden";document.getElementById("page-input").getElementsByClassName("btn_next")[0].style.visibility="visible"}else{document.getElementById("ph-plainText").style.visibility="inherit";document.getElementById("page-input").getElementsByClassName("btn_next")[0].style.visibility="hidden"}}function setRandomText(a){switch(a){case 0:result=Encrypt.randomText(plainText,null);document.getElementById("result").textContent=result;turnToPage("page-result");break;case 1:result=Encrypt.randomText(plainText,Encrypt.symbols);document.getElementById("result").textContent=result;turnToPage("page-result");break;default:var b=document.getElementById("unit").value;if(b.length>0){result=Encrypt.randomText(plainText,b);document.getElementById("result").textContent=result;turnToPage("page-result")}else{result=Encrypt.randomText(plainText,null);document.getElementById("result").textContent=result;turnToPage("page-result")}break}}function setCaesarConfigE(b){switch(b){case 0:result=Encrypt.caesarCode(plainText,Math.floor((Math.random()*53)-26));if(result!=null){document.getElementById("result").textContent=result;turnToPage("page-result")}else{alert("很抱歉\n加密失败！")}break;case 1:var a=parseInt(document.getElementById("shift").value);if(isNaN(a)){alert("请输入数字")}else{result=Encrypt.caesarCode(plainText,a);document.getElementById("result").textContent=result;turnToPage("page-result")}break;default:var a=parseInt(document.getElementById("shift").value);if(isNaN(a)){result=Encrypt.caesarCode(plainText,Math.floor((Math.random()*53)-26));if(result!=null){document.getElementById("result").textContent=result;turnToPage("page-result")}else{alert("很抱歉\n加密失败！")}}else{result=Encrypt.caesarCode(plainText,a);document.getElementById("result").textContent=result;turnToPage("page-result")}break}}function setAESConfig(a){if(a){result=Encrypt.aes_128_ecb(plainText,document.getElementById("aespassword").value);document.getElementById("result").textContent=result}else{result=Decrypt.aes_128_ecb(cypherText,document.getElementById("aespassword").value);document.getElementById("result").textContent=result}turnToPage("page-result")}var cypherText="";function setCypherText(){cypherText=document.getElementById("cypherText").textContent}var selectedAlgorithmD=0;var resultD="";function setSelectedAlgorithmD(a){switch(a){case 0:turnToPage("page-caesar");break;case 1:resultD=Decrypt.base64(cypherText);if(resultD!=null){document.getElementById("result").textContent=resultD;turnToPage("page-result")}else{alert("很抱歉\n解密失败！")}break;case 2:break;case 3:result=Decrypt.dictionaryEncrypt(cypherText,Decrypt.emojiDict);document.getElementById("result").textContent=result;turnToPage("page-result");break;case 4:turnToPage("page-aes");break;default:break}selectedAlgorithmD=a}function cypherTextInputChange(){if(document.getElementById("cypherText").textContent.length>0){document.getElementById("ph-cypherText").style.visibility="hidden";document.getElementById("page-input").getElementsByClassName("btn_next")[0].style.visibility="visible"}else{document.getElementById("ph-cypherText").style.visibility="inherit";document.getElementById("page-input").getElementsByClassName("btn_next")[0].style.visibility="hidden"}}function setCaesarConfigD(b){switch(b){case 0:result=Decrypt.caesarCode(cypherText,Math.floor((Math.random()*53)-26));if(result!=null){document.getElementById("result").textContent=result;turnToPage("page-result")}else{alert("很抱歉\n解密失败！")}break;case 1:var a=parseInt(document.getElementById("shift").value);if(isNaN(a)){alert("请输入数字")}else{result=Decrypt.caesarCode(cypherText,a);document.getElementById("result").textContent=result;turnToPage("page-result")}break;default:var a=parseInt(document.getElementById("shift").value);if(isNaN(a)){result=Decrypt.caesarCode(cypherText,Math.floor((Math.random()*53)-26));if(result!=null){document.getElementById("result").textContent=result;turnToPage("page-result")}else{alert("很抱歉\n解密失败！")}}else{result=Decrypt.caesarCode(cypherText,a);document.getElementById("result").textContent=result;turnToPage("page-result")}break}};